def input_data(in_dig):
    # json_var = 'video_dir_1/visual_trend/avi_'+str(in_dig)+'/avi_'+str(in_dig)+'_json_out.json'
    json_var = 'video_dir_2/data_src/may_' + str(in_dig) + '/may_' + str(in_dig) + '_json_out.json'
